import React, {useState} from 'react'
import BtnRender from './BtnRender'
import axios from 'axios'
import Loading from '../loading/Loading'

export default function ProductItem({product, isAdmin, token, callback, setCallback}) {
	const [loading, setLoading] = useState(false)
	
	const deleteProduct = async () => {
		try {
			setLoading(true)
			const destroyImage = axios.post('/api/destroy', {public_id: product.images.public_id}, {
				headers: {Authorization: token}
			})

			const deleteProduct = axios.delete(`/api/products/${product._id}`, {
				headers: {Authorization: token}
			})

			await destroyImage
			await deleteProduct
			setLoading(false)
			setCallback(!callback)
		} catch (error) {
			alert(error.response.data.msg)
		}
	}

	if(loading) {
		return <div className='product_card'><Loading/></div>
	}

	return (
		<div className='product_card'>
			{
				isAdmin && <input type="checkbox" checked={product.checked} name="" id="" />
			}

			<img
				src={product.images.url}
				alt=''
				title={product.title}
			/>

			<div className='product_box'>
				<h2 title={product.title}>{product.title}</h2>
				<span>$ {product.price}</span>
				<p>{product.description}</p>
			</div>

			<BtnRender product={product} deleteProduct={deleteProduct}/>
		</div>
	)
}
